import React from 'react'

import '../App.css';
import { Button } from './Button';
import './Hero.css';
import  { Component } from 'react';
import Typist from 'react-typist';
import Particles from 'react-particles-js';
import 'react-typist/dist/Typist.css';
import {motion } from "framer-motion"
// import {Frame,   Scroll } from "framer"




function Hero() {






	return (


		<div className="hero-container">




			<Particles className="particle-canvas"
	params={{
	    "particles": {



	        "number": {
	            "value": 30
	        },
			"anim": {
				"speed": 0.3,
				"size_min": 0.1
			},
	        "size": {
	            "value": 1,
				"anim": {
	                "speed": 0.3,
	                "size_min": 0.1
	            }
	        }
	    },
	    "interactivity": {
	        "events": {
	            "onhover": {
	                "enable": true,
	                "mode": "repulse"
	            }
	        }
	    }
	}} />

			{/* <video src="/videos/video.mov"  type="video/quicktime"  autoPlay loop muted /> */}

			<motion.img
			 transition={{delay: 2}}
			// animate={{scale: 1.05}}

			initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
			src='images/avatar.jpg' alt="profil"></motion.img>


	<Typist cursor={{ show: false, hideWhenDone: true, hideWhenDoneDelay: 0 }}
     avgTypingDelay={1}

	 >

		<motion.h1


		>Hello friend</motion.h1>


<Typist.Backspace count={13} delay={500} />
{/* <cursor show={false} /> */}

</Typist>



<motion.p
transition={{delay: 5}}
initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}


className="name"> 
		I'm Bart	
		<p 
animate={{ rotate: 360 }}
transition={{ duration: 2 }}
initial={{ }}
animate={{  x:500, y: -500, scale:0.23 }}
exit={{ opacity: 0 }}


class="rocket">
	🚀</p> 






		 Front-end developer  from Poland 



</motion.p>










			<motion.div
			initial={{opacity: 0 }}
			transition={{ duration: 5 }}
			animate={{   opacity: 1, 
			}}



			 className="hero-btns">

			<Button 
			 className='btns' buttonStyle='btn--outline'
				buttonSize='btn--large'>
					About Me


				</Button>
				<Button className='btns' buttonStyle='btn--outline'
				buttonSize='btn--large'>
					Skills


				</Button>
				<Button className='btns' b buttonStyle='btn--outline'
				buttonSize='btn--large'>My Project


				</Button>




			</motion.div>

		</div>


	)
}

export default Hero
