	            "value": 30
	        },
			"anim": {
				"speed": 0.3,
				"size_min": 0.1
			},
	        "size": {
	            "value": 1,
				"anim": {
	                "speed": 0.3,
	                "size_min": 0.1
	            }
	        }
	    },
	    "interactivity": {
	        "events": {
	            "onhover": {
	                "enable": true,
	                "mode": "repulse"
	            }
	        }
	    }
	}} />

			{/* <video src="/videos/video.mov"  type="video/quicktime"  autoPlay loop muted /> */}

