import React, {useState} from 'react'
import {Link } from 'react-router-dom';
// import '.Navbar.css';



function Navbar() {
	const [click, setClick] = useState(false);
	const [button, setButton] = useState(true)

	const handleClick = () => setClick(!click);
	const closeMobileMenu = () => setClick(false);

	const showButton = () => {
		if(window.innerWidth <= 960){
			setButton(false)
		} else{
			setButton(true);
		}
	}

	return (
		<>
			<nav className="navbar">
				<div className="navbar-container">
				<Link to="/" className="navabar-logo">
				Obloom <i class="fab fa-pagelines"></i>


				</Link>
				<div className='menu-icon' onClick={handleClick}>
					<i className={click ? 'fas fa-times' : 'fas fa-bars'} />
				</div>
				<ul className={click ? 'nav-menu active' : 'nav-menu'}>
					<li className="nav-item">
						<Link to='/' className='nav-links' onClick={closeMobileMenu}>Home
						</Link>
					</li>
					<li className="nav-item">
						<Link to='quotes' className='nav-links' onClick={closeMobileMenu}>Quotes
						</Link>
					</li>
					<li className="nav-item">
						<Link to='posts' className='nav-links' onClick={closeMobileMenu}>Posts
						</Link>
					</li>
					<li className="nav-item">
						<Link to='sign-up' className='nav-links-mobile' onClick={closeMobileMenu}>Sign Up
						</Link>
					</li>
				</ul>
				{button && <Button buttonStyle='btn--outline'>Sign Up</Button>}
				</div>
					</nav>	

		</>
	)
}
export default Navbar
